select a.party_id,
a.limit_id,
a.limit_type, --08???? 09???? 05????
a.item_type, -- 1-???? 2-???? 3-???????? 4-????
case when a.limit_type = '08' then a.GUARANT_ORG_REAL else a.item_amt end as credit_amt,
case
when a.crd_control_type = '1' then --1-???????????????crd_control_type ?
nvl(b.used_amt1, 0)
when a.crd_control_type = '2' then --2-????
nvl(b.used_amt2, 0)
else (case when a.limit_type = '05' then a.item_used else 0 end)
end as used_amt,
case
when a.crd_control_type = '1' then
a.item_amt - nvl(b.used_amt1, 0)
when a.crd_control_type = '2' then
a.item_amt - nvl(b.used_amt2, 0)
else (case when a.limit_type = '05' then a.item_avi when a.limit_type = '08' then a.guarant_org_real else a.item_amt end)
end as available_amt,
case
when a.limit_type = '05' then
begin_date
else
item_begin_date
end as begin_date,
case
when a.limit_type = '05' then
end_date
else
item_end_date
end as end_date,
a.status_cd
from tb_crd_third_party_limit a
left join (select a.limit_id,
sum(f.summary_amt) used_amt1,
sum(f.jjye) used_amt2
from tb_crd_third_party_limit   a,
