and c.amount_id = d.amount_id
and d.amount_detail_id = e.amount_detail_id
and e.contract_id = f.contract_id
and f.summary_status_cd in ('02', '03')
group by a.limit_id) b
on b.limit_id = a.limit_id
where status_cd = '03';
