prompt Creating function BT_GET_CUST_CKAMT
prompt ===================================
prompt
create or replace function crms.BT_GET_CUST_CKAMT(partyId varchar2)
  return tb_biz_amount_detail_apply.rmb_amt%type is
  result tb_biz_amount_detail_apply.rmb_amt%type;

  kjAmt      tb_biz_amount_detail_apply.rmb_amt%type;
  partyCkAmt tb_biz_amount_detail_apply.rmb_amt%type := 0;

begin

  --?????????????
  for bizInfo in (select b.apply_id,
                         a.credit_amt --????
                    from v_crd_credit_limit a,
                         tb_biz_approve     b
                   where a.apply_id = b.apply_id
                         and b.become_effective_mark = '03'
                         and b.approve_conclusion in ('1', '3')
                         and (b.low_risk_biz_type is null or b.is_bank_team_loan is null or
                         b.is_bank_team_loan = '0')
                         and b.party_id = partyId)
  loop

    --????
    select nvl(sum(amt), 0)
      into kjamt
      from (select sum(a.bond_value) as amt
              from tb_grt_bond           a,
                   tb_grt_mortgage_basic b
             where b.surety_id = a.surety_id
                   and a.sort_type in ('090100', '090200', '090300')
                   and a.currency_cd = 'cny'
                   and b.surety_id in (select t.surety_id
                                         from tb_biz_grt_rel t
                                        where t.apply_id = bizinfo.apply_id)
            union all

            select sum(a.bill_value) as amt
              from tb_grt_bill           a,
                   tb_grt_mortgage_basic b
             where b.surety_id = a.surety_id
                   and a.sort_type = '140100'
                   and a.currency_cd = 'cny'
                   and b.surety_id in (select t.surety_id
                                         from tb_biz_grt_rel t
                                        where t.apply_id = bizinfo.apply_id)
            union all

            select sum(a.deposit_amt) as amt
              from tb_grt_deposit        a,
                   tb_grt_mortgage_basic b
             where b.surety_id = a.surety_id
                   and a.sort_type = '080100'
                   and a.deposit_type = '01'
                   and a.currency_cd = 'cny'
                   and b.surety_id in (select t.surety_id
                                         from tb_biz_grt_rel t
                                        where t.apply_id = bizinfo.apply_id)
            union all

            select sum(a.buy_price) as amt
              from tb_grt_managemoney    a,
                   tb_grt_mortgage_basic b
             where b.surety_id = a.surety_id
                   and a.sort_type = '130100'
                   and a.currency_cd = 'cny'
                   and b.surety_id in (select t.surety_id
                                         from tb_biz_grt_rel t
                                        where t.apply_id = bizinfo.apply_id)

            union all

            select sum(ta.detail_amt * t.bzjblbdy / 100) as amt
              from tb_biz_klyhcdhp_apply      t,
                   tb_biz_amount_detail_apply ta,
                   tb_biz_amount_apply        tb
             where t.amount_detail_id = ta.amount_detail_id
                   and ta.amount_id = tb.amount_id
                   and tb.apply_id = bizinfo.apply_id

            union all

            select sum(ta.detail_amt * t.bzjblbdy / 100) as amt
              from tb_biz_bh_apply            t,
                   tb_biz_amount_detail_apply ta,
                   tb_biz_amount_apply        tb
             where t.amount_detail_id = ta.amount_detail_id
                   and ta.amount_id = tb.amount_id
                   and tb.apply_id = bizinfo.apply_id

            union all

            select sum(ta.detail_amt * t.bzjblbdy / 100) as amt
              from tb_biz_gnxyz_apply         t,
                   tb_biz_amount_detail_apply ta,
                   tb_biz_amount_apply        tb
             where t.amount_detail_id = ta.amount_detail_id
                   and ta.amount_id = tb.amount_id
                   and tb.apply_id = bizinfo.apply_id

            union all

            select sum(ta.detail_amt * t.bzjblbdy / 100) as amt
              from tb_biz_jkbh_apply          t,
                   tb_biz_amount_detail_apply ta,
                   tb_biz_amount_apply        tb
             where t.amount_detail_id = ta.amount_detail_id
                   and ta.amount_id = tb.amount_id
                   and tb.apply_id = bizinfo.apply_id

            union all

            select sum(ta.detail_amt * t.bzjblbdy / 100) as amt
              from tb_biz_jkxyz_apply         t,
                   tb_biz_amount_detail_apply ta,
                   tb_biz_amount_apply        tb
             where t.amount_detail_id = ta.amount_detail_id
                   and ta.amount_id = tb.amount_id
                   and tb.apply_id = bizinfo.apply_id);

    partyCkAmt := partyCkAmt + bizInfo.credit_amt - kjAmt;

  end loop;

  result := partyCkAmt;

  return(result);

end BT_GET_CUST_CKAMT;
/

