prompt
create or replace function crms.BT_GET_FINANCE_DATA_X(partyId         in varchar2,
                                                 itemCode        in varchar2,
                                                 customerTypeCd  in varchar2,
                                                 financeTypeCd   in varchar2,
                                                 financeDeadline in date,
                                                 isTotal         number) return number as

  /*
    --itemCode ?????????
    --customerTypeCd ????--ACCCD0002
      002 ??????????
      004 ??????????????
      006 ???????????
      007 ????????????
      008 ????????????
      009 ??????????
      011 ????????????????
      012 ??????????????
      013 ??????????????
      014 ???????????????
    --financeTypeCd ????-XD_ACCCD0001?1??,2???,3??,4??
    --financeDeadline ????
    --isTotal???????????1??0?
  */
  itemValue    number(26, 8);
  exesql1      varchar2(3000);
  exesql2      varchar2(3000);
  itemCodeTemp varchar2(60) := itemCode;
  regCount     int;

begin

  --?????????????????customerTypeCd????????????????????
  select regexp_count(itemCode, '^[[:digit:]]+$|^[A-Za-z]+[[:alnum:]]+$') into regCount from dual;

  if regCount = 0 then

    exesql2 := 'select project_cd
      from (select project_cd,
                   project_name,
                   customer_type_cd project_type
              from tb_acc_finance_statement_code
            union
            select index_code project_cd,
                   index_name project_name,
                   substr(sheet_id, 0, 3) project_type
              from tb_acc_nfd_index a)
     where project_name = :1 ' || case
                 when customerTypeCd is not null then
                  ' and project_type = ''' || customerTypeCd || ''''
               end || ' and rownum = 1';

    execute immediate exesql2
      into itemCodeTemp
      using itemCode;
  end if;

  --??
  exesql1 := ' select distinct project_value
       from (select t2.item_name project_name,
               ' || case
               when isTotal = 1 then
                'pre_total_value'
               else
                'project_value'
             end || ' project_value,
               t1.project_cd,
               t3.report_id,
               t1.finance_id
          from tb_acc_finance_statement_data t1,
               tb_acc_nfd_item               t2,
               tb_acc_nfd_sheet              t3
         where t3.sheet_id = t2.sheet_id
           and t2.item_code = t1.project_cd
        union

        select s2.index_name            project_name,
               s1.index_value_data_type project_value,
               s1.index_cd              project_cd,
               s3.report_id,
               s1.finance_id
          from tb_acc_finance_index_data s1,
               tb_acc_nfd_index          s2,
               tb_acc_nfd_sheet          s3
         where s2.sheet_id = s3.sheet_id
           and s2.index_code = s1.index_cd) a,
       (select a.party_id,
               a.finance_id,
               a.finance_deadline,
               a.finance_type_cd,
               a.customer_type_cd,
               row_number() over(partition by party_id order by finance_deadline desc,update_time desc) rownums
          from tb_acc_customer_finance a
         where a.finance_status_cd = ''02''
         and a.caliber_cd = ''2''
         and a.party_id = :1 ' || case
               when financeDeadline is not null then
                ' and a.finance_deadline = ''' || financeDeadline || ''''
             end || case
               when customerTypeCd is not null then
                ' and a.customer_type_cd = ''' || customerTypeCd || ''''
             end || case
               when financeTypeCd is not null then
                ' and a.finance_type_cd = ''' || financeTypeCd || ''''
             end || '
         ) b
 where a.report_id = b.customer_type_cd
   and a.finance_id = b.finance_id
   and b.rownums = 1
   and a.project_cd = :2 ';

  --??project_name ?? project_cd

  execute immediate exesql1
    into itemValue
    using partyId, itemCodeTemp;
  return itemValue;
exception
  when no_data_found then
    return null;
  when too_many_rows then
    dbms_output.put_line(partyId || ':' || itemCodeTemp || '??1?');
    return null;
end BT_GET_FINANCE_DATA_X;
