    begindate := to_date(to_char(currdate, 'yyyy') || '0701', 'yyyyMMdd');
    enddate   := LAST_DAY(Trunc(to_date(to_char(currdate, 'yyyy') || '09', 'yyyyMM'), 'MONTH'));
  elsif currmonth in ('10', '11', '12')
  then
    begindate := to_date(to_char(currdate, 'yyyy') || '1001', 'yyyyMMdd');
    enddate   := LAST_DAY(Trunc(to_date(to_char(currdate, 'yyyy') || '12', 'yyyyMM'), 'MONTH'));
  end if;
  if flag = 'begin'
  then
    return begindate;
  elsif flag = 'end'
  then
    return enddate;
  else
    return currdate;
  end if;
end BT_GET_QUAR_DATE;
/

