create or replace function crms.update_identity(id in varchar2) return varchar2
  authid current_user is
  ret           varchar2(18);
  len           integer := length(id);
  cindex        integer := 1;
  cvalue        integer;
  sumvalue      integer := 0;
  checkCodeCalc char;
  checkCodeCurt char;
  birthday      date;

  function get_identity_checkcode(id in varchar2) return char is
    cindex        integer := 1;
    cvalue        integer;
    sumvalue      integer := 0;
    checkCodeCalc char;
  begin
    loop
      exit when cindex = 18;
      cvalue   := to_number(substr(id, cindex, 1));
      sumvalue := sumvalue + cvalue * power(2, (18 - cindex));
      cindex   := cindex + 1;
    end loop;

    case mod(sumvalue, 11)
      when 0 then
        checkCodeCalc := '1';
      when 1 then
        checkCodeCalc := '0';
      when 2 then
        checkCodeCalc := 'X';
      when 3 then
        checkCodeCalc := '9';
      when 4 then
        checkCodeCalc := '8';
      when 5 then
        checkCodeCalc := '7';
      when 6 then
        checkCodeCalc := '6';
      when 7 then
        checkCodeCalc := '5';
      when 8 then
        checkCodeCalc := '4';
      when 9 then
        checkCodeCalc := '3';
      when 10 then
        checkCodeCalc := '2';
    end case;
    return(checkCodeCalc);
  end get_identity_checkcode;
begin
  if len = 18 and regexp_like(id, '^[0-9]{17}[0-9Xx]{1}$') then
    ret := id;
    select to_date(substr(id, 7, 8), 'yyyyMMdd') into birthday from dual;
    checkCodeCalc := get_identity_checkcode(id);
    checkCodeCurt := upper(substr(id, 18, 1));
    if checkCodeCalc <> checkCodeCurt then
      ret := '0';
    end if;
  elsif len = 15 and regexp_like(id, '^[0-9]{15}$') then
    select to_date('19' || substr(id, 7, 6), 'yyyyMMdd')
      into birthday
      from dual;
    ret := substr(id, 1, 6) || '19' || substr(id, 7, 9);
    ret := ret || get_identity_checkcode(ret);
  else
    ret := '0';
  end if;
  if (instr(ret, 'x') > 0) then
    ret := replace(ret, 'x', 'X');
  end if;
  return(ret);
exception
  when others then
    ret := '0';
    return(ret);
end update_identity;
/

