  --???????
  overdueGrade varchar2(5);                          --????
  guarantyType varchar2(5);                          --????
  status       varchar2(6);                          --??????
begin
  --???????????
  for conInfo in (select c.contract_id,                            --??ID
                         c.contract_num,                           --????
                         c.party_id,                               --???ID
                         c.main_guaranty_type,                     --?????
                         c.org_num,                                --????
                         c.cls_result,                             --??????
                         p.cust_property_status riskBizType        --??????
                    from tb_con_contract_info c,                   --?????
                         tb_csm_party         p                    --????
                   where c.con_status = '03'                       --?????03???
                     and  ( exists (select 1 from tb_loan_summary ts
                                  where ts.contract_id=c.contract_id and ts.summary_status_cd in('02','03')
                                    and (ts.yqts > 0 or ts.fljg is null or ts.auto_fljg is null)
                                  ) or c.cls_result is null)
                     and c.party_id = p.party_id
                     )
  loop

    --????????????01??
    guarantyType := coalesce(conInfo.main_guaranty_type, '01');    --??????01??????


    --????????????????
    last_cla_method := '';
    last_cls_result := '';
    last_cls_date := null;
    temp_con_counts := 0;

    select count(*)
      into temp_con_counts
      from tb_cla_debt_detailinfo t1                          --???????
     where t1.contract_num = conInfo.contract_num             --????
           and t1.status in ( '0','1');                       --???0?1

    if temp_con_counts = 1 then
      select t.cla_method,                                    --????
             t.cls_result,                                     --????
             t.create_time                                     --????
        into last_cla_method,
             last_cls_result,
             last_cls_date
        from tb_cla_debt_detailinfo t                         --???????
       where t.contract_num = conInfo.contract_num            --????
             and t.status in ( '0','1');                      --???0?1
    end if;

    --???????????
    for loanInfo in (select nvl(s1.yqts, 0) overduedays,           --????
                            s1.summary_status_cd,                  --????
                            s1.adjust_flg,                         --??????
                            s1.auto_fljg,                          --??????
                            s1.if_data_move,                       --??????
                            s3.product_cd,                         --????
                            s3.product_type,                       --?????1???2??
                            s1.summary_id,                         --??ID
                            s1.summary_num,
                            s1.fljg                                --??????
                       from tb_loan_summary s1,                    --?????
                            tb_loan_info    s2,                    --?????
                            tb_sys_product  s3                     --?????
                      where s1.loan_id = s2.loan_id                --??ID
                        and s2.product_type = s3.product_cd               --????
                     --   and s1.summary_status_cd in ('02', '03')    --?????02??,03??/??,04??
                        and (s1.summary_status_cd = '03' or (s1.summary_status_cd = '02' and s1.fljg is null))
                        and s1.contract_id = conInfo.contract_id)         --??ID

    loop
      --???????tb_sys_product.product_type,XD_GGCD033,1-?? 2-??
      --????discou?????????
      if loanInfo.product_cd in ('01006001', '01006002','01006010') then
        conInfo.riskBizType := 'sheet';                  --???????sheet????
     --   guarantyType        := '01';                     --??????????01??
      elsif loanInfo.product_type = '2' then             --?????1???2??
        conInfo.riskBizType := 'sheet';                  --???????sheet????
      end if;

      --??????(???????)
      if loanInfo.overduedays <= 0 then
        overdueGrade := '0';
      elsif loanInfo.overduedays >= 1
            and loanInfo.overduedays <= 30 then
        overdueGrade := '1';
      elsif loanInfo.overduedays >= 31
            and loanInfo.overduedays <= 60 then
        overdueGrade := '2';
      elsif loanInfo.overduedays >= 61
            and loanInfo.overduedays <= 90 then
        overdueGrade := '3';
      elsif loanInfo.overduedays >= 91
            and loanInfo.overduedays <= 120 then
        overdueGrade := '4';
      elsif loanInfo.overduedays >= 121
            and loanInfo.overduedays <= 180 then
        overdueGrade := '5';
      elsif loanInfo.overduedays >= 181
            and loanInfo.overduedays <= 270 then
        overdueGrade := '6';
      elsif loanInfo.overduedays >= 271
            and loanInfo.overduedays <= 360 then
        overdueGrade := '7';
      elsif loanInfo.overduedays >= 361 then
        overdueGrade := '8';

      end if;

      --????????????
      loanClsResult := '' ; --?????
      temp_counts := 0 ;
      v_qxtz_counts := 0 ;

      select count(*)
        into temp_counts
        from tb_cla_class_matrix_set t                   --???????
       where t.matrix_type = conInfo.riskBizType
         and t.delay_type_cd = overdueGrade              --??????
         and t.guarantee_type = guarantyType;            --????

      if temp_counts > 0 then
        select t.cls_type_cd                             --??????
          into loanClsResult
          from tb_cla_class_matrix_set t                 --???????
         where t.matrix_type = conInfo.riskBizType
           and t.delay_type_cd = overdueGrade
           and t.guarantee_type = guarantyType;

       /*
       *????????????
       *1.?????????????.
       *2.????,????????,??????????
       *3.????,??????,?????????.
       *4.????,??????,??????????
       */
        select count(*)
          into v_qxtz_counts
          from tb_con_loan_change c                      --?????????
         where c.term_change_way in('03','04')           --???????03?? 04 ????
           and c.change_status = '03'                    --???????03???
           and c.summary_id = loanInfo.summary_id;       --??ID

        if loanInfo.auto_fljg is null then

           update tb_loan_summary                         --?????
              set auto_fljg = loanClsResult                --??????
           where summary_id = loanInfo.summary_id;       --??ID

         --????,????????,??????????
         elsif loanInfo.adjust_flg is null and v_qxtz_counts = 0 and loanClsResult <> loanInfo.auto_fljg then
           update tb_loan_summary                         --?????
              set auto_fljg = loanClsResult                --??????
           where summary_id = loanInfo.summary_id;       --??ID

         --????,??????,?????????.
         elsif loanInfo.adjust_flg is null and v_qxtz_counts = 1 and loanClsResult > loanInfo.auto_fljg then
           update tb_loan_summary                         --?????
              set auto_fljg = loanClsResult                --??????
           where summary_id = loanInfo.summary_id;       --??ID

         --????,??????,??????????
         elsif loanInfo.adjust_flg = '1' and v_qxtz_counts = 1 and loanClsResult <> loanInfo.auto_fljg then
           update tb_loan_summary                         --?????
              set auto_fljg = loanClsResult                --??????
           where summary_id = loanInfo.summary_id;       --??ID
         --????,????????,?????????????,???
/*         elsif loanInfo.adjust_flg = '1' and v_qxtz_counts = 0 and loanClsResult > loanInfo.auto_fljg then
           update tb_loan_summary                         --?????
              set auto_fljg = loanClsResult                --??????
           where summary_id = loanInfo.summary_id;       --??ID   */

         end if;

        --??????(??????)
        select count(*)
          into temp_counts
          from tb_con_loan_change c                      --?????????
         where c.term_change_way = '03'                  --???????03??
           and c.change_status = '03'                    --???????03???
           and c.summary_id = loanInfo.summary_id;       --??ID

        --????????????
        if temp_counts > 0 then
          select next_val                                --?????????
            into loanClsResult
            from (select e.dictid,                                                      --??ID
                         lead(e.dictid, 1, '0501') over(order by e.sortno) next_val     --????????????0501??
                    from eos_dict_entry e                                               --???
                   where e.dicttypeid = 'XD_FLCD0001') t                                --????ID?XD_FLCD0001????
           where t.dictid = loanClsResult;
        end if;
--      else  --??0615, ??0101??1
--        loanClsResult := '0101';
      end if;

      --???????????0501????0401??0705
      if loanClsResult = '0501' then
         loanClsResult := '0401';
      end if;

      /*
      * ??????????,
      * 1.????????,??????,????02,03?????????,??????,
      * 2.????????,??????
      * 3.???????????
      */

/*      if loanClsResult = '0501' and loaninfo.if_data_move is null then
          loanClsResult := '0401';
        end if;*/


/*      --????????????????????????
      if loanInfo.fljg <> loanClsResult
         or loanInfo.fljg is null
         or length(loanInfo.fljg) != 4 then

         update tb_loan_summary                         --?????
            set fljg = loanClsResult                    --????
          where summary_id = loanInfo.summary_id;       --??ID
      end if;*/
      /* ??????????
       *1.??????????????,????????????????????,?????
       *2.??????????????,???????????????,??????????????90?,?????
       *3.????/???: ???????????,???????????????????,???
      */
     if (last_cla_method = 'auto' and loanClsResult is not null
        and (loanInfo.fljg <> loanClsResult or loanInfo.fljg is null or length(loanInfo.fljg) != 4))
     --  or (last_cla_method in ('quar', 'usual') and to_number(last_cls_result) < to_number(loanClsResult)
     --       and last_cls_date + 90 < currentDate)
       or (last_cla_method is null and loanClsResult is not null )   then
      --???????,?????
      if loanInfo.if_data_move is null then
         update tb_loan_summary                         --?????
            set fljg = loanClsResult                    --????
         where summary_id = loanInfo.summary_id;       --??ID
       end if;
     end if;

      --????????????
      insert into tb_cla_auto_classify_result          --???????
        (acr_result_id,
         loan_summary_id,
         contract_num,
         classify_date,
         cc_grade_id,
         risk_deviate_value,
         days_overdue,
         overdueGrade,
         guarantee_type,
         isfine,
         party_id,
         create_user_num,
         create_org_num,
         cust_risk_type_cd,
         auto_cla_result)
      values
        (sys_guid(),
         loanInfo.summary_id,         --????ID
         conInfo.contract_num,        --????
         currentDate,                 --????
         '',                          --??????ID
         '',                          --?????
         loanInfo.overduedays,        --????
         overdueGrade,                --??????
         guarantyType,                --????
         '',                          --????
         conInfo.party_id,            --???ID
         'nigth',                     --???
         'nigth',                     --????
         conInfo.riskBizType,         --??????
         loanClsResult);              --??????

    end loop;

    --???????????????????
    select '0' || max(to_number(a.fljg))
      into conClsResult
      from tb_loan_summary a                           --?????
     where a.summary_status_cd in ('02', '03')   --?????02???03??/???04??
           and a.contract_id = conInfo.contract_id;    --??ID

    --??????????????????????????
    if conClsResult = '0' then
      conClsResult := '0101';                                 --?????0101??1
    end if;

    --?????????????4????????????
    if length(conInfo.Cls_Result) != 4
       or conInfo.Cls_Result is null then

      --???????????0501????0401??0705
      if conClsResult = '0501' then
         conClsResult := '0401';
      end if;

      update tb_con_contract_info i                           --?????
         set i.cls_result = conClsResult                      --????
       where i.contract_id = conInfo.contract_id;             --??ID
    end if;


    --????????????????
    --??????
    --1.???? and ?????????
    --2.???? and ????????
    --3.???????
    --4.???????????(???)
    --last_cla_method???????auto  ????????usual  ?????quar  ????
    --20170824 ????: ???? and ?????????????? and ?????? + 90? > ????
    /*
    *??????????
    *1.?????????, ?????????,????????????,???.
    *2.?????????, ????????????,??????????90?,???.
    *3.????, ?????????????,??????????,???
    */
    if (last_cla_method = 'auto' and to_number(last_cls_result) != to_number(conClsResult) )
     --  or (last_cla_method in ('quar', 'usual')
     -- and to_number(last_cls_result) < to_number(conClsResult)
     -- and last_cls_date + 90 < currentDate
     -- )
       or temp_con_counts = 0 then

      --?????????
      update tb_con_contract_info i             --?????
         set i.cls_result = conClsResult        --????
       where i.contract_id = conInfo.contract_id;    --??ID

      --1???'0-???'????'4-????'??
      update tb_cla_debt_detailinfo             --???????
         set status = '4'                       --???4
       where status = '0'                       --???0
         and contract_num = conInfo.contract_num;    --????

      --???????0???????1??
      select count(*)
        into temp_counts
        from tb_cla_debt_detailinfo d           --???????
       where d.status in ( '0','1')             --???0?1
         and contract_num = conInfo.contract_num;    --????

      if temp_counts > 0 then
        status := '4';
      else
        status := '0';
      end if;

      --2??????????
      insert into tb_cla_debt_detailinfo        --???????
        (debt_detailinfo_id,
         party_id,
         ac_apply_id,
         cla_approve_num,
         loan_summary_id,
         cust_risk_type_cd,
         cla_properties,
         cla_method,
         last_cls_result,
         last_cla_method,
         cls_result,
         alc_info_id,
         ald_info_id,
         status,
         create_time,
         user_num,
         org_num,
         generate_type_cd,
         whether_square,
         contract_num)
      values
        (sys_guid(),
         conInfo.party_id,          --???ID
         '',                        --??????ID
         '',                        --???????
         '',                        --????ID
         conInfo.Riskbiztype,       --??????
         '',                        --????
         'auto',                    --?????????
         last_cls_result,           --??????
         last_cla_method,           --??????
         conClsResult,              --????
         '',                        --????????
         '',                        --????????
         status,                    --???status?????
         currentDate,
         '???',
         conInfo.org_num,
         '',
         '',
         conInfo.contract_num);
    end if;
  end loop;

  --??20????????
  --  execute immediate 'truncate table tb_cla_auto_classify_result';
  delete tb_cla_auto_classify_result r          --???????
   where r.classify_date < currentDate - 20;    --????
  COMMIT;

exception
  when others then
    resCode := SQLCODE;
