  v_remind_org_num      varchar2(20) := 'error';
  v_remind_user_num     varchar2(20) := 'error';

  /**
  YP_YJCD0002 00  ???
  YP_YJCD0002 01  ???
  YP_YJCD0002 02  ???
  YP_YJCD0002 03  ?????
  **/
  v_remind_status tb_tab_remind_info.remind_status%type := '00';

begin
  --?????
  select count(*)
    into total
    from tb_tab_remind_info r
   where r.begin_date = v_begin_date
         and r.end_date = v_end_date
         and r.business_id = v_business_id
         and r.remind_type = v_remind_type;

  if total = 0 then
