    --????ID?????/???
    select count(1)
      into total
      from (select org_num,
                   user_num
              from tb_csm_management_team c
             where c.user_placing_cd = '01'
               and c.party_id = v_party_id) t;

    if total = 1 then

      select  org_num,
              user_num
        into v_remind_org_num,
             v_remind_user_num
        from tb_csm_management_team c
       where c.user_placing_cd = '01'
         and c.party_id = v_party_id;

    end if;

   if v_remind_type = '06' then
     v_remind_status := '01';
   end if;

    --????
    insert into tb_tab_remind_info
      (remind_id,
       party_id,
       remind_type,
       create_date,
       remind_org_num,
       remind_user_num,
       remind_position,
       remind_status,
       business_id,
       update_time,
       begin_date,
       end_date)
    values
      (v_remind_id,
       v_party_id,
       v_remind_type,
       worktime,
       v_remind_org_num,
       v_remind_user_num,
       '', --??
       v_remind_status, --00-???
       v_business_id,
       worktime,
       v_begin_date,
       v_end_date);

    --????
    if v_remind_type = '02' then

      --????????
      select max(check_date)
        into v_last_time_checkdate
        from tb_aft_normal_check
       where party_id = v_business_id;

      --???????
      insert into tb_tab_daycheck_remind
        (remind_id,
         daycheck_id,
         crd_limit,
         crd_balance,
         last_time_checkdate,
         this_time_checkdate,
