    v_pns_itr_in_otd_itr := 0;
    v_pns_itr_out_otd_itr := 0;
    v_cpd_itr_in_otd_itr := 0;
    v_cpd_itr_out_otd_itr := 0;
    v_overdueitr := 0;
    v_overdueitr_in := 0;
    v_overdueitr_out := 0;
    ---????????
    select count(1)
