        into v_overdueCapital,
             v_overdueitr,
             v_overdueitr_in,
             v_overdueitr_out
       from sn_sup_debt_info_n
       where due_num = loanInfo.Due_Num and rcv_date <> to_char(workdate + 1,'yyyymmdd');
     else
       v_overdueCapital := loanInfo.Overduecapital;
    end if;

    --?????????
    select sum(dft_prn_otd_itr) dft_prn_otd_itr,
           sum(dft_itr_out_otd_itr) dft_itr_out_otd_itr,
           sum(nor_itr_out_otd_itr) nor_itr_out_otd_itr,
