           sum(pns_itr_in_otd_itr) pns_itr_in_otd_itr,
           sum(pns_itr_out_otd_itr) pns_itr_out_otd_itr,
           sum(cpd_itr_in_otd_itr) cpd_itr_in_otd_itr,
           sum(cpd_itr_out_otd_itr) cpd_itr_out_otd_itr
      into dft_prn_otd_itr,        --???????????
           dft_itr_out_otd_itr,    --?????????????
           nor_itr_out_otd_itr,    --??????????????
           dft_itr_in_otd_itr,     --?????????????
           nor_itr_in_otd_itr,      --??????????????
           v_pns_itr_in_otd_itr,       --???????????
           v_pns_itr_out_otd_itr ,     --???????????
           v_cpd_itr_in_otd_itr,       --???????????
           v_cpd_itr_out_otd_itr       --???????????
      from sn_sup_debt_info_n      --????????
     where due_num = loanInfo.due_num;

    ---------??????????------------------

    --?????0???(???2)
    if loanInfo.ceas_flg = '0' then
      v_normal_itr := loanInfo.in_nor_bal_06 + loanInfo.out_nor_bal_09;
      v_arrear_itr := loanInfo.in_dft_bal_07 + loanInfo.out_dft_bal_10;
      v_punish_itr := loanInfo.in_pns_bal_08 + loanInfo.out_pns_bal_11 + dft_prn_otd_itr +
                      dft_itr_in_otd_itr + dft_itr_out_otd_itr + nor_itr_in_otd_itr +
                      nor_itr_out_otd_itr; --(??????????)

      --????01?????
      if loanInfo.prm_cls = '01' then
        --????A00????????????????????20170607
        --???????0??????????
        if loanInfo.deva_sts = '0' then
          --???????
          v_dft_itr_in := loanInfo.in_nor_bal_06 + loanInfo.in_dft_bal_07 +
                          loanInfo.in_pns_bal_08 + dft_prn_otd_itr + dft_itr_in_otd_itr +
                          nor_itr_in_otd_itr;
          --???????1?????(???????)
        elsif loanInfo.deva_sts = '1' then
          --???????
          v_dft_itr_out := loanInfo.out_nor_bal_09 + loanInfo.out_dft_bal_10 +
                           loanInfo.out_pns_bal_11 + dft_prn_otd_itr + dft_itr_out_otd_itr +
                           nor_itr_out_otd_itr;
        end if;
      --????02????????????
      elsif loanInfo.prm_cls = '02' then
        --???????
        v_dft_itr_out := loanInfo.out_nor_bal_09 + loanInfo.out_dft_bal_10 +
                         loanInfo.out_pns_bal_11 + dft_prn_otd_itr + dft_itr_out_otd_itr +
                         nor_itr_out_otd_itr;
      end if;

    --?????1??(???1)
    elsif loanInfo.ceas_flg = '1' then
      v_normal_itr := loanInfo.in_nor_bal_06 + loanInfo.out_nor_bal_09;
      v_arrear_itr := loanInfo.in_dft_bal_07 + loanInfo.out_dft_bal_10;
      v_punish_itr := loanInfo.in_pns_bal_08 + loanInfo.out_pns_bal_11; --????

      --????01?????
      if loanInfo.prm_cls = '01' then
        --????A00????????????????????20170607
        --???????0??????????-???1
        if loanInfo.deva_sts = '0' then
          --???????
          v_dft_itr_in := loanInfo.in_nor_bal_06 + loanInfo.in_dft_bal_07 +
                          loanInfo.in_pns_bal_08;
        --???????1?????(???????)-???2
        elsif loanInfo.deva_sts = '1' then
          --???????
          v_dft_itr_out := loanInfo.out_nor_bal_09 + loanInfo.out_dft_bal_10 +
                           loanInfo.out_pns_bal_11;
        end if;

      --????02????????????
      elsif loanInfo.prm_cls = '02' then
        --???????
        v_dft_itr_out := loanInfo.out_nor_bal_09 + loanInfo.out_dft_bal_10 +
                         loanInfo.out_pns_bal_11;
      end if;
    end if;

    if temp_count > 0 then
        v_arrear_itr := v_overdueitr ;
        v_dft_itr_in := v_overdueitr_in ;
        v_dft_itr_out := v_overdueitr_out ;
      end if;
    ----------------------??????------------------------

    --????01?????
    if loanInfo.prm_cls = '01' then
       v_ljfx_in  := dft_prn_otd_itr + dft_itr_in_otd_itr + nor_itr_in_otd_itr; --??????
       v_ljfx_out := dft_itr_out_otd_itr + nor_itr_out_otd_itr; --??????
    elsif loanInfo.deva_sts = '1'         --???????1?????(???????)-???2
       or loanInfo.prm_cls = '02'         --????02????????????
     then
       v_ljfx_in  := dft_itr_in_otd_itr + nor_itr_in_otd_itr; --??????
       v_ljfx_out := dft_prn_otd_itr + dft_itr_out_otd_itr + nor_itr_out_otd_itr; --??????
    end if;

    ----------------------????????????------------------------

    --??????????
    for debtInfo in (select due_num,       --????
                            beg_date,      --????
                            end_date,      --????
                            pay_date,      --???????
                            curr_peri,     --????,
                            rcv_prn,       --??????
                            pad_up_prn,    --??????
                            rcv_nor_itr_in + rcv_nor_itr_out as rcv_nor_itr,             --??????
                            pad_up_nor_itr_in + pad_up_nor_itr_out as pad_up_nor_itr,    --??????
                            rcv_dft_itr_in + rcv_dft_itr_out as rcv_dft_itr,             --??????
