      judgeOverdueAmt := v_peri_overdue_capi + v_peri_overdue_itr + v_peri_punish_itr;

      --?????0???(???2)
      if loanInfo.ceas_flg = '0' then
        judgeOverdueAmt := judgeOverdueAmt + debtInfo.dft_otd_itr;
