      --??
      if judgeOverdueAmt > 0
         and to_date(debtInfo.end_date, 'yyyyMMdd') <= workdate then

        overdueStatus := '03'; --????

        if v_peri_overdue_capi > 0
           and (v_peri_overdue_itr + v_peri_punish_itr) > 0 then
          overdueType := '????';
        elsif v_peri_overdue_capi > 0 then
          overdueType := '????';
        elsif v_peri_overdue_itr + v_peri_punish_itr > 0 then
          overdueType := '????';
        end if;

      --??
      elsif judgeOverdueAmt = 0 then
        --?????
        if to_date(debtInfo.end_date, 'yyyyMMdd') < to_date(debtInfo.pay_date, 'yyyyMMdd') then
          overdueStatus := '04';                  --????
          overdueType   := '????';
