      select count(*)
        into total
        from tb_loan_illegal_record r             --?????
       where r.summary_num = debtInfo.due_num     --????
             and r.curr_peri = debtInfo.curr_peri;--??

      --?????????
      if total = 1 then
        update tb_loan_illegal_record             --?????
           set status          = overdueStatus,
               overdue_date    = to_date(debtInfo.End_Date, 'yyyyMMdd'),
               repay_date      = to_date(debtInfo.pay_date, 'yyyyMMdd'),
               rcv_prn         = debtInfo.rcv_prn,
               pad_up_prn      = debtInfo.pad_up_prn,
               rcv_nor_itr     = debtInfo.rcv_nor_itr,
               pad_up_nor_itr  = debtInfo.pad_up_nor_itr,
               rcv_dft_itr     = debtInfo.rcv_dft_itr,
               pad_up_dft_itr  = debtInfo.pad_up_dft_itr,
               rcv_pns_itr     = debtInfo.rcv_pns_itr,
               pad_up_pns_itr  = debtInfo.pad_up_pns_itr,
               dft_otd_itr     = debtInfo.dft_otd_itr,
               overdue_capi    = v_peri_overdue_capi,
               overdue_itr     = v_peri_overdue_itr,
               overdue_pns_itr = v_peri_punish_itr,
               update_time     = worktime,
               remark          = overdueType
         where summary_num = debtInfo.due_num
           and curr_peri = debtInfo.Curr_Peri;

      --???????????03?????04????
      elsif overdueStatus in ('03', '04') then
        insert into tb_loan_illegal_record        --?????
          (summary_num,
           curr_peri,
           status,
           party_id,
           begin_date,
           end_date,
           overdue_date,
           repay_date,
           rcv_prn,
           pad_up_prn,
           rcv_nor_itr,
           pad_up_nor_itr,
           rcv_dft_itr,
           pad_up_dft_itr,
           rcv_pns_itr,
           pad_up_pns_itr,
           dft_otd_itr,
           overdue_capi,
           overdue_itr,
           overdue_pns_itr,
           user_num,
           org_num,
           create_time,
           update_time,
           remark)
        values
          (debtInfo.Due_Num,
           debtInfo.Curr_Peri,
           overdueStatus,
           loanInfo.party_id,
           to_date(debtInfo.beg_date, 'yyyyMMdd'),
           to_date(debtInfo.End_Date, 'yyyyMMdd'),
           to_date(debtInfo.End_Date, 'yyyyMMdd'),
           to_date(debtInfo.Pay_Date, 'yyyyMMdd'),
           debtInfo.rcv_prn,
           debtInfo.pad_up_prn,
           debtInfo.rcv_nor_itr,
           debtInfo.pad_up_nor_itr,
           debtInfo.rcv_dft_itr,
           debtInfo.pad_up_dft_itr,
           debtInfo.rcv_pns_itr,
           debtInfo.pad_up_pns_itr,
           debtInfo.dft_otd_itr,
           v_peri_overdue_capi,
           v_peri_overdue_itr,
           v_peri_punish_itr,
           loanInfo.user_num,
           loanInfo.org_num,
           worktime,
           worktime,
           overdueType);
      end if;
    end loop;
    commit;

    ----------------------????????????------------------------
    --??????
    select count(*)
      into v_ljyqcs
      from tb_loan_illegal_record n   --?????
     where n.summary_num = loanInfo.due_num;

    --??????
    select count(*)
      into v_lxyqcs
      from tb_loan_illegal_record n   --?????
     where n.summary_num = loanInfo.due_num
           and n.status = '03';

