    if loanInfo.sts in ('A', '9') then
      v_summary_status := '04'; --??
    elsif loanInfo.sts in ('7') then
      v_summary_status := '07'; --???
    elsif v_lxyqcs > 0 then
      v_summary_status := '03'; --??/??
