prompt
create or replace procedure crms.bt_deal_lcjdtz (v_processId In varchar2, v_posiCode In varchar2, v_orgNum In varchar2, v_emgNum  in varchar2) as


/*v_processId VARCHAR2(32);
v_posiCode VARCHAR2(32);
v_orgNum VARCHAR2(32) ;
v_emgNum VARCHAR2(32); */

v_workitemid VARCHAR2(50);
v_activityinstid VARCHAR2(50);

BEGIN


dbms_output.put_line('---------------------------------');
dbms_output.put_line('v_processId='||v_processId||'  v_posiCode='||v_posiCode||'  v_orgNum='||v_orgNum||'  v_emgNum='||v_emgNum);

  SELECT WORKITEMID
    INTO v_workitemid
   FROM (SELECT WORKITEMID
           FROM WFWORKITEM@db_account_bps
           WHERE PROCESSINSTID=v_processId AND ENDTIME IS NULL ORDER BY CREATETIME DESC)
   WHERE rownum=1;
dbms_output.put_line('v_workitemid='||v_workitemid);

 IF v_workitemid IS NOT NULL AND LENGTH(v_workitemid)>0 THEN

      SELECT ACTIVITYINSTID
        INTO v_activityinstid
      FROM (SELECT a.ACTIVITYINSTID
              FROM WFWIPARTICIPANT@db_account_bps i
              INNER JOIN WFACTIVITYINST@db_account_bps a
              ON i.ACTIVITYINSTID=a.ACTIVITYINSTID
             WHERE i.PROCESSINSTID=v_processId
             AND i.WORKITEMID=v_workitemid
             AND a.ENDTIME IS NULL
             ORDER BY a.CREATETIME DESC)
      WHERE rownum=1;
dbms_output.put_line('v_activityinstid='||v_activityinstid);

      IF v_activityinstid IS NOT NULL AND LENGTH(v_activityinstid)>0 THEN
        UPDATE tb_wfm_processinstance
          SET APPOINT_ORG_CD=v_orgNum,
              PROCESS_STATUS='run'
        WHERE PROCESS_ID=v_processId;

       delete from tb_wfm_workiteminstance 
       where process_id = v_processid
        and work_instance_id = v_workitemid 
        and finish_time is null;
        
        UPDATE WFWORKITEM@db_account_bps
           SET PARTICIPANT=v_emgNum,
               ACTIVITYDEFID=v_posiCode,
               CURRENTSTATE='10'
        WHERE PROCESSINSTID=v_processId
          AND WORKITEMID=v_workitemid;

        UPDATE WFWIPARTICIPANT@db_account_bps
           SET ACTIVITYDEFID=v_posiCode,
               PARTIINTYPE='EXE',
               CURRENTSTATE='10',
               PARTICIPANT=v_emgNum,
               GLOBALID='P{'||v_emgNum||'}',
               PARTICIPANTID=v_emgNum
         WHERE WORKITEMID=v_workitemid
           AND PROCESSINSTID=v_processId ;

        UPDATE WFACTIVITYINST@db_account_bps
           SET ACTIVITYDEFID=v_posiCode
        WHERE PROCESSINSTID=v_processId
          AND ACTIVITYINSTID=v_activityinstid;

        dbms_output.put_line('over...');
      END IF;
 END IF;

COMMIT;

end bt_deal_lcjdtz;
