prompt
create or replace procedure crms.DEAL_IMPORTANT_CHECK is

  workdate     date := bt_get_batchdate;
  worktime     date := bt_get_batchtime;
  v_begin_date date;
  v_end_date   date;
  advancedays  int;
  v_remind_id  varchar2(32);

begin
  --????????

  --??????????????????????????????????????????15?

  for conCheck in (select s1.party_id,
                          s1.contract_id,
                          min(s2.overdue_date) first_overdue_date1,
                          min(s3.create_date) first_overdue_date2,
                          least(nvl(min(s2.overdue_date), to_date('29991231', 'yyyyMMdd')),
                                nvl(min(s3.create_date), to_date('29991231', 'yyyyMMdd'))) first_overdue_date
                     from tb_loan_summary s1
                     left join tb_loan_illegal_record s2
                       on s1.summary_num = s2.summary_num
                          and s2.status = '03'
                     left join tb_lst_info s3
                       on s3.party_id = s1.party_id
                          and s3.list_status in ('1', '2')
                    where s1.summary_status_cd in ('02', '03')
                    group by s1.party_id,
                             s1.contract_id)
  loop

    --?????????????;?????????????
    --???first_overdue_date -- first_overdue_date + 1?
    --?T??first_overdue_date + 1 -- ??15??
    --?T+1????16? -- ???15??
    --?T+2?...

    for i in 0 .. months_between(workdate, conCheck.first_overdue_date) + 1
    loop
      if i = 0 then
        v_begin_date := conCheck.first_overdue_date;
        v_end_date   := conCheck.first_overdue_date + 1;
      elsif i = 1 then
        v_begin_date := v_end_date + 1;
        v_end_date   := trunc(add_months(v_begin_date, i), 'mm') + 14;
      else
        v_begin_date := v_end_date + 1;
        v_end_date   := add_months(v_begin_date, 1) - 1;
      end if;

      -- dbms_output.put_line(v_begin_date || ' -- ' || v_end_date);

      ins_check_remind_new('14',
                       conCheck.Party_Id,
                       conCheck.Contract_Id,
                       v_begin_date,
                       v_end_date,
                       v_remind_id);

    end loop;

  end loop;

  /*********************/
  /**
  YP_YJCD0002 00  ???
  YP_YJCD0002 01  ???
  YP_YJCD0002 02  ???
  YP_YJCD0002 03  ?????
  **/

  select to_number(remind_val)
    into advancedays
    from tb_sys_remind_para
   where remind_type = '14';

  --??????
  --1.???00 -->  ???01
  update tb_tab_remind_info r
     set r.remind_status = '01',
         r.update_time   = worktime
   where r.remind_type = '14'
         and r.begin_date <= workdate --????????
         and r.remind_status = '00';

  --2.???01 -->  ???02
  update tb_tab_remind_info r
     set r.remind_status = '02',
         r.update_time   = worktime
   where r.remind_type = '14'
         and r.remind_status = '01'
         and exists (select 1
            from tb_aft_point_check
           where check_status = '03'
