   where ta.approve_id = tb.approve_id
         and tb.amount_id = tc.amount_id
         and ta.party_id = partyId
         and ta.become_effective_mark = '03'
         and ta.approve_conclusion in ('1', '3')
         and tc.product_type in ('01002001', '01002002', '01002003');

  if total > 0 then
    v_default_rate     := '01';
    v_customer_type_cd := 'estate';
  else
    --2.?????(school_hospital)???
    --???????????????????????????????????
    select count(t.party_id)
      into total
      from (select f.party_id,
                   f.customer_type_cd,
                   row_number() over(partition by f.party_id order by f.finance_deadline desc, f.update_time desc, f.caliber_cd desc) rownums
              from tb_acc_customer_finance f
             where f.finance_status_cd = '02'
                   and f.party_id = partyId
