      v_customer_type_cd := 'school_hospital';

    else
      --?????????????????

      --?????????
      select count(1)
        into total
        from tt_check_lowrisk_cust;

      if total = 0 then
        random_record_daycheck(1);
      end if;

      --3.?????(low_risk)??

      select count(1)
        into total
        from tt_check_lowrisk_cust c
       where c.party_id = partyId;

      if total > 0 then
        v_default_rate     := '12';
        v_customer_type_cd := 'low_risk';

      else
        --??????????????????

        --?????????
        select count(1)
          into total
          from tt_check_notlowrisk_cust;

        if total = 0 then
          random_record_daycheck(0);
        end if;

        --4.??????(not_low_risk)???
        select count(1)
          into total
          from tt_check_notlowrisk_cust c
         where c.party_id = partyId;

        if total > 0 then
          v_default_rate     := '03';
          v_customer_type_cd := 'not_low_risk';
