      end if;
    end if;
  end if;

  if v_default_rate != 'null' then

    select count(1)
      into total
      from tb_aft_inspect_rate
     where party_id = partyId;

    if total > 0 then

      update tb_aft_inspect_rate r
         set r.default_rate     = v_default_rate,
             r.customer_type_cd = v_customer_type_cd
       where r.party_id = partyId;

    else

      insert into tb_aft_inspect_rate
        (ir_id,
         default_rate,
         approve_rate,
         set_rate,
         customer_type_cd,
         orgn_name,
         orgn_num,
         party_name,
         party_num,
         user_num,
         org_num,
         is_different_place,
         party_id)
