create or replace procedure crms.BT_DEAL_SUMMARY_bak is

  workdate            date := bt_get_batchdate;
  --worktime            date := bt_get_worktime;

begin

  --?????????????????
  for loanInfo in (select a.summary_id,
                          a.summary_num,
                          a.summary_status_cd,
                          b.rcv_date
                     from tb_loan_summary     a       --?????
                     left join (select due_num,rcv_date
                                  from (select due_num,
                                               rcv_date ,
                                               row_number() over (partition by due_num order by rcv_date desc) rn
                                         from SN_SUP_REPAYMENT_INFO ) t
                                where rn = 1 ) b
                      on a.summary_num = b.due_num
                    where 1 = 1
                          and (
                               ( a.summary_status_cd = '04' and a.settle_date is null and b.rcv_date is not null) --???,???????
                          or (a.summary_status_cd in ('02', '03') and b.rcv_date is not null
                              and (
                                 (a.last_pay_date is not null and to_date(b.rcv_date,'YYYYMMDD') > a.last_pay_date)
                              or a.last_pay_date is null)
                              )
                              ) --?????02???03??/???04??
                         )
  loop

    --????

    update tb_loan_summary
        set settle_date = to_date(loanInfo.rcv_date,'YYYYMMDD') ,
            last_pay_date = to_date(loanInfo.rcv_date,'YYYYMMDD')
    where summary_id = loanInfo.summary_id
      and summary_status_cd = '04';

    update tb_loan_summary
        set last_pay_date = to_date(loanInfo.rcv_date,'YYYYMMDD')
    where summary_id = loanInfo.summary_id
      and summary_status_cd in ('02','03');

  end loop;




  --?????????????????
  for loanyqInfo in (select a.summary_id,
                            a.summary_num,
                            a.summary_status_cd,
                            b.bjoverdue_date,
                            c.lxoverdue_date
                     from tb_loan_summary     a       --?????
                     left join ( select summary_num,
                                        overdue_date bjoverdue_date
                                 from (
                                      select SUMMARY_NUM,
                                             OVERDUE_DATE,
                                             row_number() over(partition by summary_num order by overdue_date asc) rn
                                       from tb_loan_illegal_record
                                      where status = '03'
                                        and OVERDUE_CAPI > 0
                                     ) t where rn = 1
                                 ) b --????
                      on a.summary_num = b.summary_num
                     left join ( select summary_num,
                                        overdue_date lxoverdue_date
                                 from (
                                      select SUMMARY_NUM,
                                             OVERDUE_DATE,
                                             row_number() over(partition by summary_num order by overdue_date asc) rn
                                       from tb_loan_illegal_record
                                      where status = '03'
                                        and OVERDUE_ITR > 0
                                     ) t where rn = 1
                                 ) c   --????
                      on a.summary_num = c.summary_num

                    where a.summary_status_cd = '03'  --?????02???03??/???04??
                  )
  loop

    if loanyqInfo.Bjoverdue_Date is not null then
      update tb_loan_summary
        set BJ_YQTS = nvl(floor(workdate - loanyqInfo.bjoverdue_date + 1), 0)
      where summary_num = loanyqInfo.Summary_Num ;
    end if;

    if loanyqInfo.lxoverdue_date is not null then
      update tb_loan_summary
        set LX_YQTS = nvl(floor(workdate - loanyqInfo.lxoverdue_date + 1), 0)
      where summary_num = loanyqInfo.Summary_Num ;
    end if;


  end loop;
commit;


end BT_DEAL_SUMMARY_bak;
