prompt
create or replace procedure crms.BT_SYNCH_BASE_RATE is

  yearRate    tb_loan_loanrate.year_rate%type;
  overdueRate tb_loan_loanrate.year_rate%type;
  itrDate     varchar2(8);                     --??????
  yearStr     varchar(4) := to_char(bt_get_batchdate, 'yyyy');         --??
  monthNum    number(2) := to_number(to_char(bt_get_batchdate, 'mm')); --??
  quar        varchar(1) := to_char(bt_get_batchdate, 'q');            --??

  dictName varchar2(20);
  primary  varchar2(32);
  currDate varchar2(8) := to_char(bt_get_batchdate, 'yyyyMMdd');
  currTime date := bt_get_worktime;
  total    int;
  workdate date := bt_get_batchdate;
begin

  --?????????????????????????
  for res in (select t2.int_rate,
                     t1.*
                from (select b.summary_num,
                             b.loan_id,
                             b.org_num,
                             substr(b.org_num, 0, 2) prv_cod,
                             decode(d.product_type, '01013001', '02', '01') prm_cls,    ---?????
                             decode(d.product_type, '01006001', '010', '000') ast_cls,  ---???
                             case
                               when ceil(months_between(b.end_date, b.begin_date)) <= 6 then
                                '1'
                               when ceil(months_between(b.end_date, b.begin_date)) > 6
                                    and ceil(months_between(b.end_date, b.begin_date)) <= 12 then
                                '2'
                               when ceil(months_between(b.end_date, b.begin_date)) > 12
                                    and ceil(months_between(b.end_date, b.begin_date)) <= 36 then
                                '3'
                               when ceil(months_between(b.end_date, b.begin_date)) > 36
                                    and ceil(months_between(b.end_date, b.begin_date)) <= 60 then
                                '4'
                               when ceil(months_between(b.end_date, b.begin_date)) > 60 then
                                '5'
                             end int_rate_cd,                --
                             a.ir_update_frequency,          --
                             a.base_rate_value,              --????
                             a.year_rate,                    --????
                             a.overdue_rate_up_proportion,   --????????%
                             a.float_way,                    --
                             a.rate_float_proportion,         --
                             a.new_base_rate_value,
                             d.loan_org                       --????
                        from tb_loan_loanrate a,             --???????
                             tb_loan_summary  b,             --?????
                             tb_loan_info     d              --?????
                       where a.loan_id = b.loan_id
                             and d.loan_id = b.loan_id
                             and a.rate_type = '2'           --????
                             and a.float_type = '0'          --?????
                             and a.ir_update_frequency in ('01', '02', '03', '04','06')
                             and b.summary_status_cd in ('02', '03')) t1,
                     tb_sys_basic_rate t2
               where t1.int_rate_cd = t2.int_rate_cd
                     and t2.status = '1'
                     and ((t2.int_rate <> nvl(t1.base_rate_value, 0) and t1.new_base_rate_value is null) --???????,???????????
                     or (--t2.int_rate <> nvl(t1.base_rate_value, 0) and
                     t1.new_base_rate_value is not null and t2.int_rate <> t1.new_base_rate_value ) --????????,???????,??????????
                     ))
  loop

    --??????
    if res.ir_update_frequency = '01' --????:1.1/2.1/3.1...
     then
      itrDate := to_char(last_day(workdate) + 1, 'yyyyMMdd');
    elsif res.ir_update_frequency = '02' --????1.1/4.1/7.1/10.1
     then
      if quar = '1' then
        itrDate := yearStr || '0401';
      elsif quar = '2' then
        itrDate := yearStr || '0701';
      elsif quar = '3' then
        itrDate := yearStr || '1001';
      elsif quar = '4' then
        itrDate := yearStr + 1 || '0101';
      end if;
    elsif res.ir_update_frequency = '03' --?????1.1/6.1
     then
      if monthNum > 6 then
        --???
        itrDate := yearStr + 1 || '0101';
      else
        itrDate := yearStr || '0701';
      end if;
    elsif res.ir_update_frequency = '04' --????1.1
     then
      itrDate := yearStr + 1 || '0101';
    elsif res.ir_update_frequency = '06' --????,????????
      then
        itrDate := to_char(workdate + 1,'yyyyMMdd');
    end if;

    --??????
    if res.float_way = '1' then
      yearRate := round(res.int_rate * (1 + nvl(res.rate_float_proportion,0) / 100), 6); --??
    elsif res.float_way = '2' then
      yearRate := round(res.int_rate * (1 - nvl(res.rate_float_proportion,0) / 100), 6); --??
    end if;

    --??????
    overdueRate := round(yearRate * (1 + nvl(res.overdue_rate_up_proportion,0) / 100), 6);

    --????????????????????????
/*    update tb_loan_loanrate
       set base_rate_value = res.int_rate,
           year_rate       = yearRate,
           update_time     = currTime
     where loan_id = res.loan_id;*/

    update tb_loan_loanrate
       set new_base_rate_value = res.int_rate,
           new_year_rate = yearRate,
           valid_date = itrDate
     where loan_id = res.loan_id;
     commit;

   --????????????????????????-????????
     update tb_loan_loanrate a
       set   base_rate_value = new_base_rate_value ,
             year_rate = new_year_rate,
             update_time = currTime
     where a.valid_date is not null
       and a.loan_id = res.loan_id
       and a.valid_date = to_char(workdate + 1, 'yyyymmdd');
     commit;

    primary := sys_guid();

    select dictname               --????
      into dictName               --????
      from eos_dict_entry         --???
     where dicttypeid = 'XD_SXCD1148'             --?????
           and dictid = res.ir_update_frequency;  --?????01??02??03???04???

    --????????????/????????
    select count(1)
           into total
   --from tb_sup_intr_rate_adjust@db_account_sn t
     from aplusview.tb_sup_intr_rate_adjust t
    where t.due_num=res.summary_num
          and t.itr_date=itrDate;

    if total = 1 then
 --  update tb_sup_intr_rate_adjust@db_account_sn
     update aplusview.tb_sup_intr_rate_adjust
      set rcv_date=currDate,
          leg_per_cod = '9999',
          prv_cod = res.prv_cod,
          opn_dep = res.org_num,
          tal_dep = res.loan_org,
          prm_cls = res.prm_cls,
          ast_cls = res.ast_cls,
          nor_itr_rate = yearRate,
          del_itr_rate = overdueRate,
          cpd_itr_rate = overdueRate,
          deal_flg = '0'
          where due_num = res.summary_num
               and itr_date = itrDate;
    else
    --insert into tb_sup_intr_rate_adjust@db_account_sn      --???????????
    insert into aplusview.tb_sup_intr_rate_adjust      --???????????
      (rcv_date,
       leg_per_cod,
       prv_cod,
       opn_dep,
       tal_dep,
       due_num,
       prm_cls,
       ast_cls,
       nor_itr_rate,
       del_itr_rate,
       cpd_itr_rate,
       itr_date,
       deal_flg)
    values
      (currDate,          --????
       '9999',            --????
       res.prv_cod,       --????
       res.org_num,       --????
       res.loan_org,       --????
       res.summary_num,   --????
       res.prm_cls,       --?????
       res.ast_cls,       --?????
