begin
  --??????????????????????
  if TRANSFERWAY = '2' then
    --????????????
    delete from tb_csm_management_team tt
     where tt.party_id in (select t.party_id from tb_biz_approve t)
       and tt.user_placing_cd = '02'
       and tt.user_num = USERNUM
