                              AND TB.APPROVE_ID = APPROVEID)
                      AND T.USER_NUM = OLDUSERNUM
                      AND T.ORG_NUM = OLDORGNUM)

   LOOP
    v_contractId := CONTRACT.CONTRACT_ID;
    --??????
    UPDATE TB_CON_CONTRACT_INFO CCI
       SET CCI.USER_NUM = USERNUM, CCI.ORG_NUM = ORGNUM
     WHERE CCI.CONTRACT_ID = v_contractId
       AND CCI.USER_NUM = OLDUSERNUM
