       set tls.user_num = USERNUM, tls.org_num = ORGNUM
     where tls.loan_id in
           (select tli.loan_id
              from tb_loan_info tli
             where contract_id = v_contractId)
       and tls.user_num = OLDUSERNUM
       and tls.org_num = OLDORGNUM;

    --????????
    update tb_con_subcontract sub
       set sub.org_num = ORGNUM, sub.user_num = USERNUM
     where sub.org_num = OLDORGNUM
       and sub.user_num = OLDUSERNUM
       and sub.subcontract_id in
           (select sub.subcontract_id
              from tb_con_subcontract     sub,
                   tb_con_subcontract_rel rel,
                   tb_con_contract_info   con
             where rel.subcontract_id = sub.subcontract_id
               and rel.contract_id = con.contract_id
               and con.contract_id = v_contractId);
    --???????????????????????????????
    update tb_grt_reg_card c
       set c.org_num = ORGNUM
     where c.surety_key_id in
           (select c.surety_key_id
              from tb_grt_card_mortagage m, tb_grt_reg_card c
             where m.surety_key_id = c.surety_key_id
               and m.contract_id = v_contractId);

  END LOOP;

