  overdueType         varchar2(20);
  judgeOverdueAmt     number(26, 8); --??????????
  v_summary_status    varchar2(6);

begin

  for loanInfo in (select b.due_num,
                          b.sts, --????
                          b.ceas_flg, --????
                          b.prm_cls, --???
                          b.ast_cls, --???
                          b.deva_sts, --????
                          b.nor_itr_rate, --??
                          a.loan_id,
                          a.party_id,
                          a.org_num,
                          a.user_num,
                          c.nor_bal_01 + c.dvl_bal_02 - c.res_nor overdueCapital, --????
                          nvl(c.out_nor_bal_09, 0) out_nor_bal_09, --??????
                          nvl(c.out_dft_bal_10, 0) out_dft_bal_10, --??????
                          nvl(c.in_dft_bal_07, 0) in_dft_bal_07, --??????
                          nvl(c.out_pns_bal_11, 0) out_pns_bal_11, --????
                          nvl(c.in_pns_bal_08, 0) in_pns_bal_08, --????
                          nvl(c.in_nor_bal_06, 0) in_nor_bal_06, --??????
                          nvl(c.nor_bal_01, 0) nor_bal_01, --????
                          nvl(c.dvl_bal_02, 0) dvl_bal_02, --??????
                          nvl(c.nor_bal_01, 0) + nvl(c.dvl_bal_02, 0) v_jjye --????
                     from tb_loan_summary                a,
                          tb_sup_loan_info@db_account_sn    b,
                          tb_sup_account_info c
                    where a.summary_num = b.due_num
                          and c.due_num = b.due_num
                          and a.summary_num = summaryNum
                          and a.summary_status_cd in ('02', '03', '04'))
  loop

    --????
    v_jjye        := 0;
    v_dft_itr_in  := 0;
    v_dft_itr_out := 0;
    v_ljfx_in     := 0;
    v_ljfx_out    := 0;

    select sum(dft_prn_otd_itr) dft_prn_otd_itr,
           sum(dft_itr_out_otd_itr) dft_itr_out_otd_itr,
           sum(nor_itr_out_otd_itr) nor_itr_out_otd_itr,
           sum(dft_itr_in_otd_itr) dft_itr_in_otd_itr,
           sum(nor_itr_in_otd_itr) nor_itr_in_otd_itr
      into dft_prn_otd_itr, --???????????
           dft_itr_out_otd_itr, --?????????????
           nor_itr_out_otd_itr, --??????????????
           dft_itr_in_otd_itr, --?????????????
           nor_itr_in_otd_itr --??????????????
      from tb_sup_debt_info_n@db_account_sn
     where due_num = loanInfo.due_num;

    ---------??????????------------------

