prompt ===============================
prompt
CREATE OR REPLACE PROCEDURE CRMS.XFE_TRANSFER(v_transferId IN VARCHAR2) IS
v_active NUMBER;
v_new_loan_org VARCHAR2(32);
v_new_org_id VARCHAR2(32);
v_new_user_id VARCHAR2(32);
BEGIN
  SELECT COUNT(1) NUM,NEW_LOAN_ORG,NEW_ORG_ID,NEW_USER_ID INTO v_active,v_new_loan_org,v_new_org_id,v_new_user_id FROM TB_CSMXFE_TRANSFER
  WHERE TRANSFER_ID=v_transferId AND XFE_BIZ='1' AND STATUS='90' GROUP BY NEW_LOAN_ORG,NEW_ORG_ID,NEW_USER_ID;
  IF v_active = 1 THEN

  FOR apps IN(
SELECT xa.DETAIL_ID APPROVE_ID,a.APPLY_ID,NVL(v_new_user_id,xa.OLD_USER_ID)NEW_USER_ID
FROM TB_CSMXFE_DETAIL xa
INNER JOIN TB_BIZ_APPROVE a ON a.APPROVE_ID=xa.DETAIL_ID
WHERE xa.TRANSFER_ID=v_transferId AND xa.DETAIL_TYPE='A0' AND xa.STATUS='0'
  ) LOOP
UPDATE TB_BIZ_APPLY SET USER_NUM=apps.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE APPLY_ID=apps.APPLY_ID;
--UPDATE TB_BIZ_APPLY_MIDDLE SET USER_NUM=apps.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE APPLY_ID=apps.APPLY_ID;
UPDATE TB_BIZ_ATTACHED SET USER_NUM=apps.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE APPLY_ID=apps.APPLY_ID;
UPDATE TB_BIZ_CREDIT_LINE_MEASURE SET USER_NUM=apps.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE ID=apps.APPLY_ID;
UPDATE TB_CON_CREDIT_INFO c SET c.USER_NUM=apps.NEW_USER_ID,c.ORG_NUM=v_new_org_id WHERE c.APPLY_ID=apps.APPLY_ID;
UPDATE TB_BIZ_FAMILY_FINANCE c SET c.USER_NUM=apps.NEW_USER_ID,c.ORG_NUM=v_new_org_id WHERE c.APPLY_ID=apps.APPLY_ID;
UPDATE TB_BIZ_APPROVE SET USER_NUM=apps.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE APPROVE_ID=apps.APPROVE_ID;
UPDATE TB_CSMXFE_DETAIL SET STATUS='1' WHERE TRANSFER_ID=v_transferId AND DETAIL_ID=apps.APPROVE_ID AND DETAIL_TYPE='A0';
  END LOOP;
  FOR cons IN (
SELECT xc.DETAIL_ID CONTRACT_ID,NVL(v_new_user_id,xc.OLD_USER_ID)NEW_USER_ID FROM TB_CSMXFE_DETAIL xc
WHERE xc.TRANSFER_ID=v_transferId AND xc.DETAIL_TYPE='C0' AND xc.STATUS='0'
    ) LOOP
UPDATE TB_CON_CONTRACT_INFO c SET c.USER_NUM=cons.NEW_USER_ID,c.ORG_NUM=v_new_org_id WHERE c.CONTRACT_ID=cons.CONTRACT_ID;
UPDATE TB_GRT_IN c SET c.USER_NUM=cons.NEW_USER_ID,c.ORG_NUM=v_new_org_id WHERE c.CONTRACT_ID=cons.CONTRACT_ID;
UPDATE TB_GRT_OUT c SET c.USER_NUM=cons.NEW_USER_ID,c.ORG_NUM=v_new_org_id WHERE c.CONTRACT_ID=cons.CONTRACT_ID;
UPDATE TB_CSMXFE_DETAIL SET STATUS='1' WHERE TRANSFER_ID=v_transferId AND DETAIL_ID=cons.CONTRACT_ID AND DETAIL_TYPE='C0';
  END LOOP;

  FOR summs IN (
SELECT ls.LOAN_ID,xl.DETAIL_ID SUMMARY_ID,NVL(v_new_loan_org,xl.STR_1)NEW_LOAN_ORG,NVL(v_new_user_id,xl.OLD_USER_ID)NEW_USER_ID
FROM TB_CSMXFE_DETAIL xl
INNER JOIN TB_LOAN_SUMMARY ls on ls.SUMMARY_ID=xl.DETAIL_ID
WHERE xl.TRANSFER_ID=v_transferId AND xl.DETAIL_TYPE='L0' AND xl.STATUS='0'
  ) LOOP
UPDATE TB_LOAN_INFO SET USER_NUM=summs.NEW_USER_ID,ORG_NUM=v_new_org_id,LOAN_ORG=summs.NEW_LOAN_ORG WHERE LOAN_ID=summs.LOAN_ID;
UPDATE TB_LOAN_SUMMARY SET USER_NUM=summs.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE LOAN_ID=summs.LOAN_ID;
UPDATE TB_CSMXFE_DETAIL SET STATUS='1' WHERE TRANSFER_ID=v_transferId AND DETAIL_ID=summs.SUMMARY_ID  AND DETAIL_TYPE='L0';
  END LOOP;

  FOR subs IN (
SELECT xg.DETAIL_ID SUBCONTRACT_ID,NVL(v_new_user_id,xg.OLD_USER_ID)NEW_USER_ID
FROM TB_CSMXFE_DETAIL xg WHERE xg.TRANSFER_ID=v_transferId AND xg.DETAIL_TYPE='G0' AND xg.STATUS='0'
  ) LOOP
UPDATE TB_CON_SUBCONTRACT SET USER_NUM=subs.NEW_USER_ID,ORG_NUM=v_new_org_id WHERE SUBCONTRACT_ID=subs.SUBCONTRACT_ID;
UPDATE TB_CSMXFE_DETAIL SET STATUS ='1' WHERE TRANSFER_ID=v_transferId AND DETAIL_ID=subs.SUBCONTRACT_ID AND DETAIL_TYPE='G0';
  END LOOP;

  FOR mgt1 IN (
SELECT xg.DETAIL_ID SURETY_ID,NVL(v_new_user_id,xg.OLD_USER_ID)NEW_USER_ID FROM TB_CSMXFE_DETAIL xg
WHERE xg.TRANSFER_ID=v_transferId AND xg.DETAIL_TYPE='G1' AND xg.STATUS='0'
