mapper.select:
select a.ira_apply_id, b.party_name, to_char(c.rating_dt,'yyyy-mm-dd') as RATING_DT, a.rating_type, c.credit_rating_cd, to_char(c.effective_start_dt,'yyyy-mm-dd') as EFFECTIVE_START_DT, to_char(c.effective_end_dt,'yyyy-mm-dd') as EFFECTIVE_END_DT, c.rating_state, c.user_num, c.org_num from tb_irm_internal_rating_apply a, tb_irm_internal_rating_result c, tb_csm_party b where a.party_id = b.party_id and a.ira_apply_id = c.ira_apply_id and a.party_id = #{partyId,jdbcType=VARCHAR} and c.rating_state IN( '03')
and a.rating_type in ('1','2','3')
and a.rating_type='4'
and c.USER_NUM= #{userNum,jdbcType=VARCHAR}
and c.ORG_NUM= #{orgNum,jdbcType=VARCHAR}
order by c.RATING_STATE

select MODEL_ID, MODEL_NAME, MODEL_TYPE_CD, RATING_MODEL_VER, MODEL_STATE, START_DATE, EXPIRATION_DT, MODEL_NUM, MODEL_SUIT_SCALE, CREATE_TIME, APPROVAL_DATE, DEPLOY_DATE, USER_NUM, APPROVAL_USER_NUM, DEPLOY_USER_NUM from TB_IRM_RATING_MODEL where 1 = 1
and MODEL_ID= #{modelId,jdbcType=VARCHAR}
and MODEL_TYPE_CD= #{modelTypeCd,jdbcType=VARCHAR}
and RATING_MODEL_VER= #{ratingModelVer,jdbcType=VARCHAR}
and MODEL_STATE= #{modelState,jdbcType=VARCHAR}

select INDEX_ID, INDEX_DESC, INDEX_TYPE, INDEX_NAME, INDEX_CODE, MODEL_ID, INDEX_WEIGHT, PROPERTY_TYPE_CD from TB_IRM_MODEL_INDEX where 1 = 1
and MODEL_ID= #{modelId,jdbcType=VARCHAR}
and INDEX_TYPE= #{indexType,jdbcType=VARCHAR}
and INDEX_CODE= #{indexCode,jdbcType=VARCHAR}

select a.INDEX_ID,a.PROPERTY_TYPE_CD,a.INDEX_NAME,b.INDEX_NUM,a.INDEX_DESC from TB_IRM_INDEX_SCORE b,TB_IRM_MODEL_INDEX a where 1=1 and a.INDEX_ID = b.INDEX_ID
and a.INDEX_ID = #{indexId,jdbcType=VARCHAR}
and b.INDEX_NUM = #{indexNum,jdbcType=VARCHAR}
and a.PROPERTY_TYPE_CD != '0204' order by a.INDEX_ID desc


     select t4.* from TB_IRM_INTERNAL_RATING_APPLY t1,tb_irm_rating_model t2 ,TB_IRM_MODEL_INDEX t3,tb_irm_rating_fin_index t4
 where t1.RATING_MODEL_CD=t2.MODEL_TYPE_CD and t2.MODEL_ID=t3.MODEL_ID and t4.INDEX_CD=t3.INDEX_CODE and 
IRA_APPLY_ID= #{applyId,jdbcType=VARCHAR}


    select RATING_PARA_ID,PARA_TYPE,PARA_CONTENT from TB_IRM_RATING_PARA 
	where 1 = 1

 		and RATING_PARA_ID = #{ratingParaId,jdbcType=VARCHAR}


 		and PARA_TYPE = #{paraType,jdbcType=VARCHAR}

	    select max(a.adjust_value) as adjustValue  from tb_irm_adjust_options a,tb_irm_adjust_option b where a.adj_opt_id=b.ao_id and adjust_type_cd ='04' and is_adjust in(01,02) and ira_apply_id =#{applyId,jdbcType=VARCHAR}
    	union all
    	select max(a.adjust_value) as adjustValue from tb_irm_adjust_options a,tb_irm_adjust_option b where a.adj_opt_id=b.ao_id and adjust_type_cd ='04' and is_adjust in(03,04) and ira_apply_id =#{applyId,jdbcType=VARCHAR}
    	union all
    	select max(a.adjust_value) as adjustValue from tb_irm_adjust_options a,tb_irm_adjust_option b where a.adj_opt_id=b.ao_id and adjust_type_cd !='04' and is_adjust in(01,02) and ira_apply_id =#{applyId,jdbcType=VARCHAR}
    	union all
    	select max(a.adjust_value)  as adjustValue from tb_irm_adjust_options a,tb_irm_adjust_option b where a.adj_opt_id=b.ao_id and adjust_type_cd !='04' and is_adjust in(03,04) and ira_apply_id =#{applyId,jdbcType=VARCHAR}

select scale_def_id as scaleDefId, credit_rating_cd as creditRatingCd, credit_rating_def as creditRatingDef, min_pd as minPd, max_pd as maxPd, avag_pd as avagPd, credit_rating_display as creditRatingDisplay, small_ltd_display as smallLtdDisplay, order_no as orderNo, is_professional_loan as isProfessionalLoan from Tb_Irm_Scale_Def where 1 = 1
AND credit_rating_display = #{creditRatingDisplay,jdbcType=VARCHAR}
AND order_no = #{orderNo,jdbcType=VARCHAR}
AND credit_rating_cd = #{creditRatingCd,jdbcType=VARCHAR}
AND small_ltd_display = #{smallLtdDisplay,jdbcType=VARCHAR}

select tor.OR_ID as orId, tor.USER_NUM as userNum, tor.ORG_NUM as orgNum, tor.OVERTHROW_REASON as overthrowReason, tor.BEFORE_GRADE as beforeGrade, tor.AFTER_GRADE as afterGrade, tor.ira_apply_id as iraApplyId, tor.IS_OVERTHROW as isOverthrow, tor.overthrow_dt as overthrowDt, tor.POST_CD as postCd from tb_irm_overthrow_record tor where 1=1
and tor.ira_apply_id = #{iraApplyId,jdbcType=VARCHAR}
and tor.BEFORE_GRADE= #{beforeGrade,jdbcType=VARCHAR}
and tor.AFTER_GRADE= #{afterGrade,jdbcType=VARCHAR}
and tor.POST_CD= #{postCd,jdbcType=VARCHAR}
and tor.USER_NUM= #{userNum,jdbcType=VARCHAR}
order by tor.overthrow_dt desc

select tor.ORG_NUM as orgNum, tor.POST_CD as postCd, tor.BEFORE_GRADE as overthrowReason, tor.ira_apply_id as iraApplyId, tor.overthrow_dt as overthrowDt from tb_irm_overthrow_record tor where 1=1 and rownum = 1
and tor.ira_apply_id = #{iraApplyId,jdbcType=VARCHAR}
and tor.POST_CD= #{postCd,jdbcType=VARCHAR}
order by tor.overthrow_dt desc

select tor.ORG_NUM as orgNum, tor.POST_CD as postCd, tor.AFTER_GRADE as overthrowReason, tor.ira_apply_id as iraApplyId, tor.overthrow_dt as overthrowDt from tb_irm_overthrow_record tor where 1=1
and tor.ira_apply_id = #{iraApplyId,jdbcType=VARCHAR}
order by tor.overthrow_dt desc
