SELECT
L.ORG_NUM, L.USER_NUM, V.PARTY_NUM, V.PARTY_ID, V.PARTY_NAME, V.CERT_NUM, V.CERT_TYPE, S.SUMMARY_NUM, C.CONTRACT_NUM, TO_DATE(R.RCV_DATE,'yyyymmdd') AS RCV_DATE, (NVL(PAD_UP_PRN,0)+NVL(PAD_UP_NOR_ITR_IN,0)+NVL(PAD_UP_DFT_ITR_IN,0)+NVL(PAD_UP_PNS_ITR_IN,0)+NVL(PAD_UP_NOR_ITR_OUT,0)+NVL(PAD_UP_DFT_ITR_OUT,0)+NVL(PAD_UP_PNS_ITR_OUT,0)) HKJE, NVL(PAD_UP_PRN,0) HBJE, (NVL(PAD_UP_NOR_ITR_IN,0)+NVL(PAD_UP_DFT_ITR_IN,0)+NVL(PAD_UP_PNS_ITR_IN,0)+NVL(PAD_UP_NOR_ITR_OUT,0)+NVL(PAD_UP_DFT_ITR_OUT,0)+NVL(PAD_UP_PNS_ITR_OUT,0)) HXJE, S.FLJG, NVL(PAD_UP_NOR_ITR_IN,0)+NVL(PAD_UP_DFT_ITR_IN,0)+NVL(PAD_UP_NOR_ITR_OUT,0)+NVL(PAD_UP_DFT_ITR_OUT,0) AS SUM1, NVL(PAD_UP_PNS_ITR_IN,0)+NVL(PAD_UP_PNS_ITR_OUT,0) AS SUM2, NVL(PAD_UP_PRN,0)+NVL(PAD_UP_NOR_ITR_IN,0)+NVL(PAD_UP_DFT_ITR_IN,0)+NVL(PAD_UP_NOR_ITR_OUT,0)+NVL(PAD_UP_DFT_ITR_OUT,0)+NVL(PAD_UP_PNS_ITR_IN,0)+NVL(PAD_UP_PNS_ITR_OUT,0) AS SUM3 FROM TB_LOAN_INFO L,TB_LOAN_SUMMARY S,TB_CON_CONTRACT_INFO C,SN_SUP_REPAYMENT_INFO R,V_CSM_PARTY V WHERE L.SUMMARY_NUM=S.SUMMARY_NUM AND L.CONTRACT_ID=C.CONTRACT_ID AND S.SUMMARY_NUM=R.DUE_NUM AND L.PARTY_ID=V.PARTY_ID AND V.PARTY_TYPE_CD IN ('01','02') AND L.ORG_NUM IN (SELECT O.ORGCODE FROM OM_ORGANIZATION O WHERE O.ORGSEQ LIKE '%'||(SELECT ORGID FROM OM_ORGANIZATION WHERE ORGCODE = #{orgNum,jdbcType=VARCHAR})||'%') AND R.RCV_DATE > 0
AND S.SUMMARY_NUM = #{summaryNum}
AND C.CONTRACT_NUM = #{contractNum}
AND V.PARTY_NAME LIKE '%'||#{partyName,jdbcType=VARCHAR}||'%'
AND V.CERT_TYPE = #{certType}
AND V.CERT_NUM = #{certNum}
AND L.USER_NUM = #{userNum}
AND to_date(R.RCV_DATE,'yyyymmdd') = to_date(#{rcvDate} ,'yyyy-mm-dd hh24:mi:ss')
ORDER BY R.RCV_DATE DESC

