SELECT ORG_NUM,ORGNAME ,ORGSEQ , SUM(CASE WHEN KK='1' THEN PARTY_ID ELSE 0 END ) AS ZCHS, SUM(CASE WHEN KK='1' THEN SUMMARY_NUM ELSE 0 END )AS ZCBS, SUM(CASE WHEN KK='1' THEN DKYE+JJYE ELSE 0 END) AS ZCYE, SUM(CASE WHEN KK='2' THEN PARTY_ID ELSE 0 END) AS GZHS, SUM(CASE WHEN KK='2' THEN SUMMARY_NUM ELSE 0 END) AS GZBS, SUM(CASE WHEN KK='2' THEN DKYE ELSE 0 END) AS GZDK, SUM(CASE WHEN KK='2' THEN JJYE ELSE 0 END) AS GZYE, SUM(CASE WHEN KK='3' THEN PARTY_ID ELSE 0 END) AS CJHS, SUM(CASE WHEN KK='3' THEN SUMMARY_NUM ELSE 0 END) AS CJBS, SUM(CASE WHEN KK='3' THEN DKYE ELSE 0 END) AS CJDK, SUM(CASE WHEN KK='3' THEN JJYE ELSE 0 END) AS CJYE, SUM(CASE WHEN KK='4' THEN PARTY_ID ELSE 0 END) AS KYHS, SUM(CASE WHEN KK='4' THEN SUMMARY_NUM ELSE 0 END) AS KYBS, SUM(CASE WHEN KK='4' THEN DKYE ELSE 0 END) AS KYDK, SUM(CASE WHEN KK='4' THEN JJYE ELSE 0 END) AS KYYE, SUM(CASE WHEN KK='5' THEN PARTY_ID ELSE 0 END) AS SSHS, SUM(CASE WHEN KK='5' THEN SUMMARY_NUM ELSE 0 END) AS SSBS, SUM(CASE WHEN KK='5' THEN DKYE ELSE 0 END) AS SSDK, SUM(CASE WHEN KK='5' THEN JJYE ELSE 0 END) AS SSYE, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN PARTY_ID ELSE 0 END) AS HJHS, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN SUMMARY_NUM ELSE 0 END) AS HJBS, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN DKYE ELSE 0 END) AS HJDK, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN JJYE ELSE 0 END) AS HJYE FROM ( SELECT S.ORG_NUM,OM.ORGNAME,OM.ORGSEQ, CASE WHEN S.FLJG IN ('0101','0102','0103') THEN '1' WHEN S.FLJG IN ('0201','0202','0203') THEN '2' WHEN S.FLJG IN ('0301','0301') THEN '3' WHEN S.FLJG='0401' THEN '4' WHEN S.FLJG='0501' THEN '5' ELSE 'X' END KK, COUNT(DISTINCT S.PARTY_ID) PARTY_ID, COUNT(S.SUMMARY_NUM) SUMMARY_NUM, SUM(CASE WHEN S.SUMMARY_STATUS_CD='03' THEN S.JJYE ELSE 0 END) DKYE, SUM(CASE WHEN S.SUMMARY_STATUS_CD='02' THEN S.JJYE ELSE 0 END) JJYE FROM TB_LOAN_SUMMARY S,TB_LOAN_INFO L ,OM_ORGANIZATION OM WHERE S.LOAN_ID=L.LOAN_ID AND S.ORG_NUM=OM.ORGCODE AND S.SUMMARY_STATUS_CD IN ('02','03') AND S.FLJG IS NOT NULL AND exists( SELECT DISTINCT SUMMARY_NUM FROM ( SELECT S.SUMMARY_NUM FROM TB_LOAN_SUMMARY S,TB_LOAN_INFO L,TB_SYS_PRODUCT P WHERE S.LOAN_ID=L.LOAN_ID AND L.PRODUCT_TYPE=P.PRODUCT_CD AND P.PRODUCT_TYPE='2' AND S.SUMMARY_STATUS_CD = '02' AND S.ORG_NUM IN (SELECT O.ORGCODE FROM OM_ORGANIZATION O WHERE O.ORGSEQ LIKE '%'||#{orgNum,jdbcType=VARCHAR}||'%')
AND S.USER_NUM = #{userNum,jdbcType=VARCHAR}
UNION ALL SELECT S.SUMMARY_NUM FROM TB_LOAN_SUMMARY S,TB_LOAN_INFO L,TB_SYS_PRODUCT P ,SN_SUP_LOAN_INFO SU WHERE S.LOAN_ID=L.LOAN_ID AND L.PRODUCT_TYPE=P.PRODUCT_CD AND S.SUMMARY_NUM = SU.DUE_NUM AND P.PRODUCT_TYPE='1' AND SU.DEVA_STS = '1' AND NVL(S.JJYE,0)>0 AND S.ORG_NUM IN (SELECT O.ORGCODE FROM OM_ORGANIZATION O WHERE O.ORGSEQ LIKE '%'||#{orgNum,jdbcType=VARCHAR}||'%')
AND S.USER_NUM = #{userNum,jdbcType=VARCHAR}
) where S.Summary_Num = SUMMARY_NUM ) GROUP BY S.ORG_NUM,OM.ORGNAME,OM.ORGSEQ, CASE WHEN S.FLJG IN ('0101','0102','0103') THEN '1' WHEN S.FLJG IN ('0201','0202','0203') THEN '2' WHEN S.FLJG IN ('0301','0301') THEN '3' WHEN S.FLJG='0401' THEN '4' WHEN S.FLJG='0501' THEN '5' ELSE 'X' END ) WHERE KK IN ('1','2','3','4','5') GROUP BY ORG_NUM ,ORGNAME,ORGSEQ UNION ALL SELECT '99999' AS ORG_NUM,'合计' AS ORGNAME ,'.10000.99.99.' AS ORGSEQ, SUM(CASE WHEN KK='1' THEN PARTY_ID ELSE 0 END ) AS ZCHS, SUM(CASE WHEN KK='1' THEN SUMMARY_NUM ELSE 0 END )AS ZCBS, SUM(CASE WHEN KK='1' THEN DKYE+JJYE ELSE 0 END) AS ZCYE, SUM(CASE WHEN KK='2' THEN PARTY_ID ELSE 0 END) AS GZHS, SUM(CASE WHEN KK='2' THEN SUMMARY_NUM ELSE 0 END) AS GZBS, SUM(CASE WHEN KK='2' THEN DKYE ELSE 0 END) AS GZDK, SUM(CASE WHEN KK='2' THEN JJYE ELSE 0 END) AS GZYE, SUM(CASE WHEN KK='3' THEN PARTY_ID ELSE 0 END) AS CJHS, SUM(CASE WHEN KK='3' THEN SUMMARY_NUM ELSE 0 END) AS CJBS, SUM(CASE WHEN KK='3' THEN DKYE ELSE 0 END) AS CJDK, SUM(CASE WHEN KK='3' THEN JJYE ELSE 0 END) AS CJYE, SUM(CASE WHEN KK='4' THEN PARTY_ID ELSE 0 END) AS KYHS, SUM(CASE WHEN KK='4' THEN SUMMARY_NUM ELSE 0 END) AS KYBS, SUM(CASE WHEN KK='4' THEN DKYE ELSE 0 END) AS KYDK, SUM(CASE WHEN KK='4' THEN JJYE ELSE 0 END) AS KYYE, SUM(CASE WHEN KK='5' THEN PARTY_ID ELSE 0 END) AS SSHS, SUM(CASE WHEN KK='5' THEN SUMMARY_NUM ELSE 0 END) AS SSBS, SUM(CASE WHEN KK='5' THEN DKYE ELSE 0 END) AS SSDK, SUM(CASE WHEN KK='5' THEN JJYE ELSE 0 END) AS SSYE, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN PARTY_ID ELSE 0 END) AS HJHS, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN SUMMARY_NUM ELSE 0 END) AS HJBS, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN DKYE ELSE 0 END) AS HJDK, SUM(CASE WHEN KK IN ('1','2','3','4','5') THEN JJYE ELSE 0 END) AS HJYE FROM ( SELECT CASE WHEN S.FLJG IN ('0101','0102','0103') THEN '1' WHEN S.FLJG IN ('0201','0202','0203') THEN '2' WHEN S.FLJG IN ('0301','0301') THEN '3' WHEN S.FLJG='0401' THEN '4' WHEN S.FLJG='0501' THEN '5' ELSE 'X' END KK, COUNT(DISTINCT S.PARTY_ID) PARTY_ID,COUNT(S.SUMMARY_NUM) SUMMARY_NUM, SUM(CASE WHEN S.SUMMARY_STATUS_CD='03' THEN S.JJYE ELSE 0 END) DKYE, SUM(CASE WHEN S.SUMMARY_STATUS_CD='02' THEN S.JJYE ELSE 0 END) JJYE FROM TB_LOAN_SUMMARY S,TB_LOAN_INFO L WHERE S.LOAN_ID=L.LOAN_ID AND S.SUMMARY_STATUS_CD IN ('02','03') AND S.FLJG IS NOT NULL AND exists( SELECT DISTINCT SUMMARY_NUM FROM ( SELECT S.SUMMARY_NUM FROM TB_LOAN_SUMMARY S,TB_LOAN_INFO L,TB_SYS_PRODUCT P WHERE S.LOAN_ID=L.LOAN_ID AND L.PRODUCT_TYPE=P.PRODUCT_CD AND P.PRODUCT_TYPE='2' AND S.SUMMARY_STATUS_CD = '02' AND S.ORG_NUM IN (SELECT O.ORGCODE FROM OM_ORGANIZATION O WHERE O.ORGSEQ LIKE '%'||#{orgNum,jdbcType=VARCHAR}||'%')
AND S.USER_NUM = #{userNum,jdbcType=VARCHAR}
UNION ALL SELECT S.SUMMARY_NUM FROM TB_LOAN_SUMMARY S,TB_LOAN_INFO L,TB_SYS_PRODUCT P ,SN_SUP_LOAN_INFO SU WHERE S.LOAN_ID=L.LOAN_ID AND L.PRODUCT_TYPE=P.PRODUCT_CD AND S.SUMMARY_NUM = SU.DUE_NUM AND P.PRODUCT_TYPE='1' AND SU.DEVA_STS = '1' AND NVL(S.JJYE,0)>0 AND S.ORG_NUM IN (SELECT O.ORGCODE FROM OM_ORGANIZATION O WHERE O.ORGSEQ LIKE '%'||#{orgNum,jdbcType=VARCHAR}||'%')
AND S.USER_NUM = #{userNum,jdbcType=VARCHAR}
) where S.SUMMARY_NUM = SUMMARY_NUM ) GROUP BY S.ORG_NUM, CASE WHEN S.FLJG IN ('0101','0102','0103') THEN '1' WHEN S.FLJG IN ('0201','0202','0203') THEN '2' WHEN S.FLJG IN ('0301','0301') THEN '3' WHEN S.FLJG='0401' THEN '4' WHEN S.FLJG='0501' THEN '5' ELSE 'X' END ) WHERE KK IN ('1','2','3','4','5') ORDER BY ORGSEQ
