mapper.select:
select '业务合同' biz_type, t.CON_STATUS, t.product_type, '' apply_id, t.amount_detail_id, t.CONTRACT_ID, t.CONTRACT_NUM, t.CONTRACT_AMT, t.CON_BALANCE, t.CREDIT_MODE, nvl(tls.con_yu_e,0) as con_yu_e, t.CURRENCY_CD as CURRENCY_CD, to_char(t.BEGIN_DATE, 'yyyy-mm-dd') as BEGIN_DATE, to_char(t.END_DATE, 'yyyy-mm-dd') as END_DATE from tb_con_contract_info t left join (select sum(nvl(jjye,0)) as con_yu_e,CONTRACT_ID from tb_loan_summary where summary_status_cd!='09' group by CONTRACT_ID) tls on t.CONTRACT_ID=tls.CONTRACT_ID where t.CON_STATUS = '03' and t.user_num= '200555' and t.org_num = '0700'
and t.PARTY_ID =#{partyId}
and t.CONTRACT_NUM like '%'||#{contractNum}||'%'

SELECT tb.biz_num, te.party_name, tb.biz_type, tb.biz_type_flag, tb.biz_happen_nature, tb.biz_happen_type, td.detail_amt, td.currency_cd, td.credit_term, td.cycle_unit, tb.valid_date, td.end_date, td.amount_detail_id, tb.approve_id, tb.apply_id, td.product_type, te.party_id, td.credit_mode, td.bo_use FROM tb_biz_approve tb, TB_BIZ_AMOUNT_DETAIL_APPROVE td, tb_csm_party te WHERE tb.APPROVE_ID = td.APPROVE_ID AND tb.party_id = te.party_id AND td.bo_use>0
AND tb.PARTY_ID = #{partyId}
AND DECODE(td.CREDIT_MODE,'02',#{userId},tb.user_num) = #{userId}
AND td.AMOUNT_DETAIL_ID = #{amountDetailId}
AND tb.BIZ_NUM LIKE '%'||#{bizNum}||'%'
AND tb.BECOME_EFFECTIVE_MARK = '03'

